    private void btnInputActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInputActionPerformed
        String WordToGuess = txtInput.getText().toUpperCase();

        Title.setText("Start guessing!");
        txtInput.setText("");

        char[] stringToCharArray = WordToGuess.toCharArray();
        
        String HiddenWord = String.valueOf(stringToCharArray);
        HiddenWord = HiddenWord.replaceAll("[a-zA-Z]", "*").trim();
        txtWord.setText(HiddenWord);
    }//GEN-LAST:event_btnInputActionPerformed

